-module(hash_service).-author('yh@gmail.com').-export([start/0, stop/0]).-export([hash_string/1]).-spec start() -> ok | {error, term()}.start() ->	application:start(hash_service).-spec stop() -> ok | {error, term()}.stop() ->	application:stop(hash_service).hash_string(Value) ->    hash_service_util:hash_string(Value).